252:     function _processBridgeData(
253:         bytes memory data,
254:         address bridgeMediatorL2,
255:         uint256 chainId
256:     ) internal
257:     {
258:         // Gnosis chains
259:         if (chainId == 100 || chainId == 10200) {
260:             // Check the L1 initial selector
261:             bytes4 functionSig = bytes4(data);
262:             if (functionSig != REQUIRE_TO_PASS_MESSAGE) {
263:                 revert WrongSelector(functionSig, chainId);
264:             }
265: 
266:             // Check if the data length is less than a size of a selector plus the message minimum payload size
267:             if (data.length < MIN_GNOSIS_PAYLOAD_LENGTH) {
268:                 revert IncorrectDataLength(data.length, MIN_GNOSIS_PAYLOAD_LENGTH);
269:             }
270: 
271:             // Copy the data without the selector
272:             bytes memory payload = new bytes(data.length - SELECTOR_DATA_LENGTH);
273:             for (uint256 i = 0; i < payload.length; ++i) {
274:                 payload[i] = data[i + 4];
275:             }
276: 
277:             // Decode the requireToPassMessage payload: homeMediator (L2), mediatorPayload (need decoding), requestGasLimit
278:             (address homeMediator, bytes memory mediatorPayload, ) = abi.decode(payload, (address, bytes, uint256));
279:             // Check that the home mediator matches the L2 bridge mediator address
280:             if (homeMediator != bridgeMediatorL2) {
281:                 revert WrongL2BridgeMediator(homeMediator, bridgeMediatorL2);
282:             }
283: 
284:             // Check the L2 initial selector
285:             functionSig = bytes4(mediatorPayload);
286:             if (functionSig != PROCESS_MESSAGE_FROM_FOREIGN) {
287:                 revert WrongSelector(functionSig, chainId);
288:             }
289: 
290:             // Copy the data without a selector
291:             bytes memory bridgePayload = new bytes(mediatorPayload.length - SELECTOR_DATA_LENGTH);
292:             for (uint256 i = 0; i < bridgePayload.length; ++i) {
293:                 bridgePayload[i] = mediatorPayload[i + SELECTOR_DATA_LENGTH];
294:             }
295: 
296:             // Decode the processMessageFromForeign payload: l2Message (executed on L2)
297:             (bytes memory l2Message) = abi.decode(bridgePayload, (bytes));
298: 
299:             // Verify processMessageFromForeign payload
300:             _verifyBridgedData(l2Message, chainId);
301:         }
302: 
303:         // Polygon chains
304:         if (chainId == 137 || chainId == 80001) {
305:             // Check the L1 initial selector
306:             bytes4 functionSig = bytes4(data);
307:             if (functionSig != SEND_MESSAGE_TO_CHILD) {
308:                 revert WrongSelector(functionSig, chainId);
309:             }
310: 
311:             // Check if the data length is less than a size of a selector plus the message minimum payload size
312:             if (data.length < MIN_POLYGON_PAYLOAD_LENGTH) {
313:                 revert IncorrectDataLength(data.length, MIN_POLYGON_PAYLOAD_LENGTH);
314:             }
315: 
316:             // Copy the data without the selector
317:             bytes memory payload = new bytes(data.length - SELECTOR_DATA_LENGTH);
318:             for (uint256 i = 0; i < payload.length; ++i) {
319:                 payload[i] = data[i + SELECTOR_DATA_LENGTH];
320:             }
321: 
322:             // Decode sendMessageToChild payload: fxGovernorTunnel (L2), l2Message (executed on L2)
323:             (address fxGovernorTunnel, bytes memory l2Message) = abi.decode(payload, (address, bytes));
324:             // Check that the fxGovernorTunnel matches the L2 bridge mediator address
325:             if (fxGovernorTunnel != bridgeMediatorL2) {
326:                 revert WrongL2BridgeMediator(fxGovernorTunnel, bridgeMediatorL2);
327:             }
328: 
329:             // Verify sendMessageToChild payload
330:             _verifyBridgedData(l2Message, chainId);
331:         }
332:     }


