091:         // Check for the correct data length
092:         uint256 dataLength = data.length;
093:         if (dataLength < DEFAULT_DATA_LENGTH) {
094:             revert IncorrectDataLength(DEFAULT_DATA_LENGTH, data.length);
095:         }
096: 
097:         // Read the proxy multisig address (20 bytes) and the multisig-related data
098:         assembly {
099:             multisig := mload(add(data, DEFAULT_DATA_LENGTH))
100:         }
101: 
102:         // Check that the multisig address corresponds to the authorized multisig proxy bytecode hash
103:         bytes32 multisigProxyHash = keccak256(multisig.code);
104:         if (proxyHash != multisigProxyHash) {
105:             revert UnauthorizedMultisig(multisig);
106:         }
107: 
108:         // If provided, read the payload that is going to change the multisig ownership and threshold
109:         // The payload is expected to be the `execTransaction()` function call with all its arguments and signature(s)
110:         if (dataLength > DEFAULT_DATA_LENGTH) {
111:             uint256 payloadLength = dataLength - DEFAULT_DATA_LENGTH;
112:             bytes memory payload = new bytes(payloadLength);
113:             for (uint256 i = 0; i < payloadLength; ++i) {
114:                 payload[i] = data[i + DEFAULT_DATA_LENGTH];
115:             }
116: 
117:             // Call the multisig with the provided payload
118:             (bool success, ) = multisig.call(payload);
119:             if (!success) {
120:                 revert MultisigExecFailed(multisig);
121:             }
122:         }
123: 
124:         // Get the provided proxy multisig owners and threshold
125:         address[] memory checkOwners = IGnosisSafe(multisig).getOwners();
126:         uint256 checkThreshold = IGnosisSafe(multisig).getThreshold();
127: 
128:         // Verify updated multisig proxy for provided owners and threshold
129:         if (threshold != checkThreshold) {
130:             revert WrongThreshold(checkThreshold, threshold);
131:         }
132:         uint256 numOwners = owners.length;
133:         if (numOwners != checkOwners.length) {
134:             revert WrongNumOwners(checkOwners.length, numOwners);
135:         }
136:         // The owners' addresses in the multisig itself are stored in reverse order compared to how they were added:
137:         // https://etherscan.io/address/0xd9db270c1b5e3bd161e8c8503c55ceabee709552#code#F6#L56
138:         // Thus, the check must be carried out accordingly.
139:         for (uint256 i = 0; i < numOwners; ++i) {
140:             if (owners[i] != checkOwners[numOwners - i - 1]) {
