709:                 topUpEligible = (IVotingEscrow(ve).getVotes(serviceOwner) >= veOLASThreshold  ||
710:                     IVotingEscrow(ve).getVotes(donator) >= veOLASThreshold) ? true : false;
711:             }
712: 
