1031:             // Update state maxBond value
1032:             maxBond = uint96(curMaxBond);
1033:             // Reset the tokenomics parameters update flag
1034:             tokenomicsParametersUpdated = 0;
1035:         }
1036:         // Update effectiveBond with the current or updated maxBond value
1037:         curMaxBond += effectiveBond;
1038:         effectiveBond = uint96(curMaxBond);
1039: 
1040:         // Update the IDF value for the next epoch or assign a default one if there are no ETH donations
1041:         if (incentives[0] > 0) {
1042:             // Calculate IDF based on the incoming donations
1043:             uint256 idf = _calculateIDF(incentives[1], tp.epochPoint.numNewOwners);
1044:             nextEpochPoint.epochPoint.idf = uint64(idf);
1045:             emit IDFUpdated(idf);
1046:         } else {
1047:             // Assign a default IDF value
1048:             nextEpochPoint.epochPoint.idf = 1e18;
1049:         }
1050: 
1051:         // Cumulative incentives
1052:         uint256 accountRewards = incentives[2] + incentives[3];
1053:         // Owner top-ups: epoch incentives for component owners funded with the inflation
1054:         incentives[5] = (inflationPerEpoch * tp.unitPoints[0].topUpUnitFraction) / 100;
1055:         // Owner top-ups: epoch incentives for agent owners funded with the inflation
1056:         incentives[6] = (inflationPerEpoch * tp.unitPoints[1].topUpUnitFraction) / 100;
1057:         // Even if there was no single donating service owner that had a sufficient veOLAS balance,
1058:         // we still record the amount of OLAS allocated for component / agent owner top-ups from the inflation schedule.
1059:         // This amount will appear in the EpochSettled event, and thus can be tracked historically
1060:         uint256 accountTopUps = incentives[5] + incentives[6];
1061: 
1062:         // Treasury contract rebalances ETH funds depending on the treasury rewards
1063:         if (incentives[1] == 0 || ITreasury(treasury).rebalanceTreasury(incentives[1])) {
1064:             // Emit settled epoch written to the last economics point
1065:             emit EpochSettled(eCounter, incentives[1], accountRewards, accountTopUps);
1066:             // Start new epoch
1067:             epochCounter = uint32(eCounter + 1);
1068:         } else {
1069:             // If the treasury rebalance was not executed correctly, the new epoch does not start
1070:             revert TreasuryRebalanceFailed(eCounter);
1071:         }
1072: 
1073:         return true;
1074:     }


