449:             revert OwnerOnly(msg.sender, owner);
450:         }
451:         
452:         // Check array length
453:         if (targets.length != selectors.length || targets.length != statuses.length || targets.length != chainIds.length) {
454:             revert WrongArrayLength(targets.length, selectors.length, statuses.length, chainIds.length);
455:         }
456: 
457:         // Traverse all the targets and selectors to build their paired values
458:         for (uint256 i = 0; i < targets.length; ++i) {
459:             // Check for zero address targets
460:             if (targets[i] == address(0)) {
461:                 revert ZeroAddress();
462:             }
463: 
464:             // Check selector for zero selector value
465:             if (selectors[i] == bytes4(0)) {
466:                 revert ZeroValue();
467:             }
468: 
469:             // Check chain Ids to be greater than zero
470:             if (chainIds[i] == 0) {
471:                 revert ZeroValue();
472:             }
473: 
474:             // Push a pair of key defining variables into one key
475:             // target occupies first 160 bits
476:             uint256 targetSelectorChainId = uint256(uint160(targets[i]));
477:             // selector occupies next 32 bits
478:             targetSelectorChainId |= uint256(uint32(selectors[i])) << 160;
479:             // chainId occupies next 64 bits
480:             targetSelectorChainId |= chainIds[i] << 192;
481: 
482:             // Set the status of the target and selector combination
483:             mapAllowedTargetSelectorChainIds[targetSelectorChainId] = statuses[i];
484:         }
485: 
486:         emit SetTargetSelectors(targets, selectors, chainIds, statuses);
487:     }


