1180:         for (uint256 i = 0; i < unitIds.length; ++i) {
1181:             // Check for the unit type to be component / agent only
1182:             if (unitTypes[i] > 1) {
1183:                 revert Overflow(unitTypes[i], 1);
1184:             }
1185: 
1186:             // Check that the unit Ids are in ascending order, not repeating, and no bigger than registries total supply
1187:             if (unitIds[i] <= lastIds[unitTypes[i]] || unitIds[i] > registriesSupply[unitTypes[i]]) {
1188:                 revert WrongUnitId(unitIds[i], unitTypes[i]);
1189:             }
1190:             lastIds[unitTypes[i]] = unitIds[i];
1191: 
1192:             // Check the component / agent Id ownership
1193:             address unitOwner = IToken(registries[unitTypes[i]]).ownerOf(unitIds[i]);
1194:             if (unitOwner != account) {
1195:                 revert OwnerOnly(unitOwner, account);
1196:             }
1197:         }
1198: 
1199:         // Get the current epoch counter
1200:         uint256 curEpoch = epochCounter;
1201: 
1202:         for (uint256 i = 0; i < unitIds.length; ++i) {
1203:             // Get the last epoch number the incentives were accumulated for
1204:             uint256 lastEpoch = mapUnitIncentives[unitTypes[i]][unitIds[i]].lastEpoch;
1205:             // Calculate rewards and top-ups if there were pending ones from the previous epoch
1206:             if (lastEpoch > 0 && lastEpoch < curEpoch) {
1207:                 // Get the overall amount of unit rewards for the component's last epoch
1208:                 // reward = (pendingRelativeReward * rewardUnitFraction) / 100
1209:                 uint256 totalIncentives = mapUnitIncentives[unitTypes[i]][unitIds[i]].pendingRelativeReward;
1210:                 if (totalIncentives > 0) {
1211:                     totalIncentives *= mapEpochTokenomics[lastEpoch].unitPoints[unitTypes[i]].rewardUnitFraction;
1212:                     // Accumulate to the final reward for the last epoch
1213:                     reward += totalIncentives / 100;
1214:                 }
1215:                 // Add the final top-up for the last epoch
1216:                 totalIncentives = mapUnitIncentives[unitTypes[i]][unitIds[i]].pendingRelativeTopUp;
1217:                 if (totalIncentives > 0) {
1218:                     // Summation of all the unit top-ups and total amount of top-ups per epoch
1219:                     // topUp = (pendingRelativeTopUp * totalTopUpsOLAS * topUpUnitFraction) / (100 * sumUnitTopUpsOLAS)
1220:                     totalIncentives *= mapEpochTokenomics[lastEpoch].epochPoint.totalTopUpsOLAS;
1221:                     totalIncentives *= mapEpochTokenomics[lastEpoch].unitPoints[unitTypes[i]].topUpUnitFraction;
1222:                     uint256 sumUnitIncentives = uint256(mapEpochTokenomics[lastEpoch].unitPoints[unitTypes[i]].sumUnitTopUpsOLAS) * 100;
1223:                     // Accumulate to the final top-up for the last epoch
1224:                     topUp += totalIncentives / sumUnitIncentives;
1225:                 }
1226:             }
1227: 
1228:             // Accumulate total rewards to finalized ones
1229:             reward += mapUnitIncentives[unitTypes[i]][unitIds[i]].reward;
1230:             // Accumulate total top-ups to finalized ones
1231:             topUp += mapUnitIncentives[unitTypes[i]][unitIds[i]].topUp;
